css:
todo {
  width: 400px;
  overflow-y: scroll;
}
todo .header {
  display: flex;
  justify-content: flex-end;
  margin-bottom: 0.5rem;
}
todo .header h1 {
  flex: 1;
  margin: 0;
  padding: 0;
}
todo .header .controls {
  padding: 0;
  margin: 0;
}
todo .search {
  background-color: #1b1d23;
  border-color: #181a1f;
  border: 0;
  width: 100%;
  padding: 0.5em;
}
todo ::-webkit-input-placeholder {
  /* WebKit, Blink, Edge */
  color: rgba(157, 165, 180, 0.6);
}
todo :-moz-placeholder {
  /* Mozilla Firefox 4 to 18 */
  color: rgba(157, 165, 180, 0.6);
  opacity: 1;
}
todo ::-moz-placeholder {
  /* Mozilla Firefox 19+ */
  color: rgba(157, 165, 180, 0.6);
  opacity: 1;
}
todo :-ms-input-placeholder {
  /* Internet Explorer 10-11 */
  color: rgba(157, 165, 180, 0.6);
}
todo .status {
  text-align: right;
  color: #6494ed;
}
todo .loading {
  margin: auto;
}
todo .list .list-item {
  margin-bottom: 10px;
}
todo .list .list-item .filepath {
  color: rgba(157, 165, 180, 0.6);
  overflow: hidden;
  text-overflow: ellipsis;
}
todo .list .list-item .todo-text {
  color: #9da5b4;
  margin: 5px 0 5px 5px;
}
todo .list .list-item .todo-text:hover {
  cursor: pointer;
  color: #d7dae0;
}
todo .icon-file-text,
todo .icon-file-directory {
  color: rgba(157, 165, 180, 0.6);
}
todo .list-item {
  white-space: normal;
}
